import React, { useState, useEffect, useRef } from 'react';
import { 
  Box, 
  Container, 
  Typography, 
  Paper, 
  TextField, 
  Button, 
  AppBar, 
  Toolbar, 
  IconButton,
  CircularProgress,
  Divider,
  useMediaQuery
} from '@mui/material';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import SendIcon from '@mui/icons-material/Send';
import DeleteIcon from '@mui/icons-material/Delete';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import { v4 as uuidv4 } from 'uuid';

// Create a theme instance with company colors
const theme = createTheme({
  palette: {
    primary: {
      main: '#2563eb', // Professional blue
    },
    secondary: {
      main: '#10b981', // Success green
    },
    background: {
      default: '#f9fafb',
    },
    error: {
      main: '#ef4444',
    }
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    h6: {
      fontWeight: 500,
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 8,
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: 12,
        },
      },
    },
  },
});

function App() {
  const [message, setMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [userId] = useState(uuidv4()); // Generate a unique ID for the user session
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  
  const messagesEndRef = useRef(null);
  
  // Scroll to the bottom of the chat when new messages are added
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };
  
  useEffect(() => {
    scrollToBottom();
  }, [chatHistory]);
  
  const sendMessage = async () => {
    if (!message.trim()) return;
    
    // Add user message to chat
    const userMessage = { role: 'user', content: message };
    setChatHistory(prev => [...prev, userMessage]);
    setMessage('');
    setLoading(true);
    
    try {
      const response = await fetch('/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message: message.trim(),
          user_id: userId
        }),
      });
      
      const data = await response.json();
      
      if (response.ok) {
        const aiMessage = { role: 'assistant', content: data.message };
        setChatHistory(prev => [...prev, aiMessage]);
      } else {
        console.error('Error:', data.error);
        setChatHistory(prev => [...prev, { 
          role: 'system', 
          content: `Error: ${data.error || 'Failed to get response from the chatbot.'}` 
        }]);
      }
    } catch (error) {
      console.error('Network error:', error);
      setChatHistory(prev => [...prev, { 
        role: 'system', 
        content: 'Network error. Please check your connection and try again.' 
      }]);
    } finally {
      setLoading(false);
    }
  };
  
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };
  
  const clearChat = async () => {
    try {
      await fetch(`/api/clear/${userId}`, {
        method: 'POST',
      });
      setChatHistory([]);
    } catch (error) {
      console.error('Error clearing chat:', error);
    }
  };
  
  return (
    <ThemeProvider theme={theme}>
      <Box className="chat-container" sx={{ bgcolor: 'background.default' }}>
        <AppBar position="static" elevation={0} sx={{ bgcolor: 'white', color: 'primary.main' }}>
          <Toolbar>
            <SmartToyIcon sx={{ mr: 2, color: 'primary.main' }} />
            <Typography variant="h6" component="div" sx={{ flexGrow: 1, fontWeight: 600 }}>
              AI Innovate Solutions
            </Typography>
            <IconButton color="primary" onClick={clearChat} title="Clear chat">
              <DeleteIcon />
            </IconButton>
          </Toolbar>
        </AppBar>
        
        <Container maxWidth="md" sx={{ flexGrow: 1, py: 2, display: 'flex', flexDirection: 'column' }}>
          <Paper 
            elevation={2} 
            sx={{ 
              flexGrow: 1, 
              p: 2, 
              mb: 2, 
              overflow: 'auto',
              display: 'flex',
              flexDirection: 'column',
              border: '1px solid #e5e7eb',
              boxShadow: 'rgba(0, 0, 0, 0.05) 0px 1px 2px 0px'
            }}
          >
            {chatHistory.length === 0 ? (
              <Box sx={{ 
                display: 'flex', 
                flexGrow: 1, 
                alignItems: 'center', 
                justifyContent: 'center',
                flexDirection: 'column',
                color: 'text.secondary',
                gap: 2,
                p: 3
              }}>
                <SmartToyIcon sx={{ fontSize: 64, color: 'primary.main', opacity: 0.8 }} />
                <Typography variant="h6" sx={{ textAlign: 'center' }}>
                  Welcome to AI Innovate Solutions
                </Typography>
                <Typography variant="body1" sx={{ textAlign: 'center', maxWidth: '600px' }}>
                  I'm your AI assistant. How can I help you with our AI app development, chatbot solutions, or machine learning services today?
                </Typography>
              </Box>
            ) : (
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                {chatHistory.map((msg, index) => (
                  <Box 
                    key={index}
                    sx={{
                      display: 'flex',
                      justifyContent: msg.role === 'user' ? 'flex-end' : 'flex-start',
                    }}
                  >
                    <Paper
                      elevation={0}
                      sx={{
                        p: 2,
                        maxWidth: isMobile ? '90%' : '80%',
                        backgroundColor: msg.role === 'user' ? '#EBF5FF' : 
                                       msg.role === 'system' ? '#FFEBEE' : '#F0FDF4',
                        borderRadius: 2,
                        border: '1px solid',
                        borderColor: msg.role === 'user' ? '#BFDBFE' : 
                                   msg.role === 'system' ? '#FECACA' : '#DCFCE7',
                      }}
                    >
                      <Typography variant="body1" whiteSpace="pre-wrap">
                        {msg.content}
                      </Typography>
                    </Paper>
                  </Box>
                ))}
                <div ref={messagesEndRef} />
              </Box>
            )}
          </Paper>
          
          <Box sx={{ display: 'flex', gap: 1 }}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Type your message..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              multiline
              maxRows={4}
              disabled={loading}
              sx={{ 
                "& .MuiOutlinedInput-root": {
                  borderRadius: 2,
                }
              }}
            />
            <Button 
              variant="contained" 
              color="primary" 
              endIcon={loading ? <CircularProgress size={20} color="inherit" /> : <SendIcon />}
              onClick={sendMessage}
              disabled={!message.trim() || loading}
              sx={{ minWidth: '100px' }}
            >
              Send
            </Button>
          </Box>
        </Container>
        
        <Box sx={{ textAlign: 'center', py: 2, mt: 'auto', borderTop: '1px solid #e5e7eb' }}>
          <Typography variant="body2" color="text.secondary">
            © {new Date().getFullYear()} AI Innovate Solutions | Your Premier AI Software Agency
          </Typography>
        </Box>
      </Box>
    </ThemeProvider>
  );
}

export default App;
